/******************************************************************GENERATE RETURN CODE***************************************************************************/		
		if($onlyFields){
			$ret = '';
			foreach((array)$scructureMap as $k => $v){
				$ret .= $allFormElements[$v];
			}
		}else{			
			$ret = '
			<div class="pq_sett_all">
				<div class="pq_icons_block">				
					{%ICON_BLOCK%}
				';
				if((int)$withoutwhitelabel == 0){
					$ret.='
						<div class="pq_branding"><p>'.$this->_dictionary[navigation][without_copyright].'</p><span>pro</span>
						<input type="checkbox" class="pq_switch" name="'.$name.'[whitelabel]" onclick="getPreviewByActiveForm(\''.$name.'\', \''.$toolContainerID.'\');" id="'.$name.'_whitelabel" '.sprintf("%s", (($valArray[whitelabel] == "on")?"checked":"")).'>
