		$ret = array();		
		foreach((array)$this->_options as $k => $v){
			if((string)$v[enable] == 'on')
			{
				if($v[position]){
					$ret[desktop][$v[position]][] = array('code'=>$k,'info'=>$array[$k][name], 'eventHandler'=>$array[$k][eventHandler], 'position'=>$v[position]);
