	function getToolInfo($name){
		$ret = '';				
		$ret[name] =  $this->_getToolName($name);		
		$ret[eventHandler] = $this->_getToolEH($name);
		$toolsStatus = $this->_getToolStatus($name, $this->_getToolProOptions($name));
