	function deleteHash(val){
		return val.toString().replace("#", "");		
	}
	
	function deleteHashAndPasteCode(prefix, val){
		if(deleteHash(val)){
			return prefix+deleteHash(val);
		}else{
			return '';
		}
	}
	
	
